// **
// *Float-menu
// **************************************
const trigger = document.querySelector(".menu-trigger");
const sideMenu = document.querySelector(".sidebar");

if (trigger && sideMenu) {
  function menuOpen() {
    sideMenu.classList.add("menu-open");
  }

  function menuClosed() {
    sideMenu.classList.remove("menu-open");
  }

  trigger.addEventListener("mouseenter", menuOpen);
  sideMenu.addEventListener("mouseleave", menuClosed);

  trigger.addEventListener("click", () => {
    sideMenu.classList.toggle("menu-open");
  });

  menuLinks = document.querySelectorAll(".menu-link-tab");
  menuLinks.forEach((link) => {
    link.addEventListener("focus", () => {
      menuOpen();
    });
    link.addEventListener("blur", () => {
      menuClosed();
    });
  });

  menuHint = document.querySelector(".menu-hint");

  menuHint.addEventListener("click", () => {
    sideMenu.classList.toggle("menu-open");

    let aboutMenuItem = document.querySelector(".menu a:first-child");
    if (aboutMenuItem) {
      aboutMenuItem.focus();
    }
  });

  menuHint.addEventListener("keydown", (event) => {
    if (event.key === "Enter") {
      sideMenu.classList.toggle("menu-open");

      let aboutMenuItem = document.querySelector(".menu a:first-child");
      if (aboutMenuItem) {
        aboutMenuItem.focus();
      }
    }
  });
}

