// **
// *Leave a message
// **************************************

if (document.body.classList.contains("page-message")) {
  const mesageForm = document.querySelector("form[name='leave_message']");

  const messageSection = document.querySelector("#messages");
  const messageList = messageSection.querySelector("ul");

  function toggleMessageSection() {
    messageSection.classList.toggle(
      "hide-message-section",
      !messageList.querySelector("li"),
    );
  }

  toggleMessageSection();

  mesageForm.addEventListener("submit", (event) => {
    event.preventDefault();

    const userName = event.target.usersName.value;
    const usersEmail = event.target.usersEmail.value;
    const usersMessage = event.target.usersMessage.value;

    console.log(`${userName}, ${usersEmail}, ${usersMessage}`);

    const newMessage = document.createElement("li");

    const messageUserEmail = document.createElement("a");
    messageUserEmail.href = `mailto:${usersEmail}`;
    messageUserEmail.setAttribute("aria-label", "Reply to user");
    messageUserEmail.textContent = `${userName}`;

    const messageText = document.createElement("span");
    messageText.textContent = `${usersMessage}`;

    const removeButton = document.createElement("button");
    removeButton.setAttribute("type", "button");
    removeButton.textContent = "Remove";
    removeButton.addEventListener("click", (event) => {
      const entry = event.target.parentNode;
      entry.remove();
      toggleMessageSection();
    });

    const editButton = document.createElement("button");
    editButton.setAttribute("type", "button");
    editButton.textContent = "Edit";
    editButton.addEventListener("click", (event) => {
      const entry = event.target.parentNode;
      const entryText = entry.querySelector("span");
      const oldMessage = entryText.textContent;

      entryText.style.display = "none";

      const inputEdit = document.createElement("input");
      inputEdit.type = "text";
      inputEdit.value = oldMessage;

      entry.insertBefore(inputEdit, editButton);
      inputEdit.focus();

      inputEdit.addEventListener("blur", (event) => {
        entryText.textContent = inputEdit.value;
        entryText.style.display = "";
        inputEdit.remove();
      });

      inputEdit.addEventListener("keydown", (event) => {
        if (event.key === "Enter") {
          inputEdit.blur();
        }
      });
    });

    newMessage.append(messageUserEmail, messageText, editButton);
    newMessage.appendChild(removeButton);

    messageList.append(newMessage);
    toggleMessageSection();

    event.target.reset();
  });
}

